import React, { useState, useEffect } from "react";
import "/Users/tobiadesanya/Slurm-feedback/src/components/VideoPair.css";

const VideoPair = () => {
  const [videos, setVideos] = useState([]);
  const [currentPair, setCurrentPair] = useState(0);
  const username = localStorage.getItem("username");

  useEffect(() => {
    // Fetch video data from backend
    fetch("http://localhost:5000/videos")
      .then((res) => res.json())
      .then((data) => setVideos(data));
  }, []);

  const handleVote = (preferredVideoId) => {
    const voteData = {
      username,
      video1_id: videos[currentPair].id,
      video2_id: videos[currentPair + 1].id,
      preferred_video_id: preferredVideoId,
    };

    fetch("http://localhost:5000/vote", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(voteData),
    }).then(() => {
      if (currentPair + 2 < videos.length) {
        setCurrentPair(currentPair + 2); // Show next pair
      } else {
        alert("Thank you for voting!"); // Voting complete
      }
    });
  };

  if (videos.length < 2) return <h1>Loading videos...</h1>;

  return (
    <div className="video-pair-container">
      <h1>Welcome, {username}</h1>
      <div className="video-container">
        <div>
          <video src={videos[currentPair].url} controls />
          <button
            onClick={() => handleVote(videos[currentPair].id)}
            className="video-button"
          >
            Select Video A
          </button>
        </div>
        <div>
          <video src={videos[currentPair + 1].url} controls />
          <button
            onClick={() => handleVote(videos[currentPair + 1].id)}
            className="video-button"
          >
            Select Video B
          </button>
        </div>
      </div>
    </div>
  );
};

export default VideoPair;
