import React, { useState, useEffect } from "react";

const VideoPair = () => {
  const [videos, setVideos] = useState([]);
  const [currentPair, setCurrentPair] = useState(0);
  const [ratings, setRatings] = useState({});
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/videos")
      .then((res) => res.json())
      .then((data) => setVideos(data));
  }, []);

  const handleVote = (preferredVideoId) => {
    const userId = localStorage.getItem("user_id");
    const voteData = {
      user_id: parseInt(userId, 10),
      video1_id: videos[currentPair].id,
      video2_id: videos[currentPair + 1].id,
      video1_rating: ratings.video1 || 0,
      video2_rating: ratings.video2 || 0,
      preferred_video_id: preferredVideoId,
    };

    fetch("http://localhost:5000/vote", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(voteData),
    }).then(() => {
      setSubmitted(true);
      alert("Thank you for voting!");
    });
  };

  if (submitted) {
    return <h1>You have submitted your votes. Thank you!</h1>;
  }

  if (videos.length < 2) {
    return <h1>Loading videos...</h1>;
  }

  return (
    <div>
      <h1>Vote on Videos</h1>
      <div>
        <video src={videos[currentPair].url} controls />
        <input
          type="number"
          min="1"
          max="5"
          onChange={(e) => setRatings({ ...ratings, video1: e.target.value })}
        />
        <button onClick={() => handleVote(videos[currentPair].id)}>Prefer This</button>
      </div>
      <div>
        <video src={videos[currentPair + 1].url} controls />
        <input
          type="number"
          min="1"
          max="5"
          onChange={(e) => setRatings({ ...ratings, video2: e.target.value })}
        />
        <button onClick={() => handleVote(videos[currentPair + 1].id)}>Prefer This</button>
      </div>
    </div>
  );
};

export default VideoPair;
